snippets.lg:
background-color: #${1:from};
background-image: -webkit-gradient(linear, left top, left bottom, from(#${1:from}), to(#${2:to}));
background-image: -webkit-linear-gradient(top, #${1:from}, #${2:to});
background-image: -o-linear-gradient(top, #${1:from}, #${2:to});
background-image: -ms-linear-gradient(top, #${1:from}, #${2:to});
background-image: -moz-linear-gradient(top, #${1:from}, #${2:to});
background-image: linear-gradient(to bottom, #${1:from}, #${2:to});

